import { useState, useEffect, useRef } from "react";
import { API_GET_DATA } from '../../global/constants'
import Edit from "./components/Edit";
import List from "./components/List";
import "./index.css";

// ...省略其他代码...

function downloadJson(jsonData, filename) {
  const jsonStr = JSON.stringify(jsonData, null, 2);
  const blob = new Blob([jsonStr], { type: "application/json" });

  const link = document.createElement("a");
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();

  URL.revokeObjectURL(link.href);
}

const Home = () => {
  const [data, setData] = useState([]);
  const submittingStatus = useRef(false);

  useEffect(() => {
    if (!submittingStatus.current) {
      return;
    }
    fetchSetData(data)
      .then(data => submittingStatus.current = false)
  }, [data]);

  useEffect(() => {
    fetchData(setData);
  }, []);

  useEffect(() => {
    if (data.length > 0) {
      // 修改文件的逻辑
      const modifiedData = data.map(item => ({
        name: item.name,
        description: item.description,
        image: item.image,
        attributes: [
          { trait_type: "number", value: item.number },
          { trait_type: "department", value: item.dep },
          { trait_type: "school", value: "國立台灣師範大學" },
          { trait_type: "address", value: item.address }
        ]
      }));

      // 保存修改后的文件
      downloadJson(modifiedData, "data.json");

      // 获取学号作为文件名
      const number = data[0].number; // 假设学号在数据的第一个对象中
      const filename = `${number}.json`; // 使用学号作为文件名，例如：学号.json

      uploadJsonToPinata(modifiedData, filename)
        .then(result => {
          console.log('File uploaded to Pinata:', result);
          // 处理上传成功后的操作
        })
        .catch(error => {
          console.error('Error uploading file to Pinata:', error);
          // 处理上传失败后的操作
        });
    }
  }, [data]);

  return (
    <div className="app">
      <Edit add={setData} submittingStatus={submittingStatus} />
      <List listData={data} deleteData={setData} submittingStatus={submittingStatus} />
    </div>
  );
};

export default Home;
