const Item = ({ id, number, name, dep, address, deleteData, title, description, img, submittingStatus }) => {
    function deleteItem() {
        submittingStatus.current = - true
        deleteData(function (prev) {
            return prev.filter(item => item.id !== id)
        })
    }
    return (
        <div className="item">
            <div>
                <p>{title}</p>
                <p>{description}</p>
                <p>{img}</p>
                <p>{number}</p>
                <p>{`${name} ${dep}`}</p>
                <p>{address}</p>
            </div>
            <button onClick={deleteItem} className="remove">刪除</button>
        </div>
    );
};

export default Item;
