import { useState, useEffect, useRef } from "react";
import { API_GET_DATA } from '../../global/constants'
import Edit from "./components/Edit";
import List from "./components/List";
import "./index.css";

// ...省略其他代码...

async function fetchSetData(data) {
  await fetch(API_GET_DATA, {
    method: "PUT",
    headers: {
      'Content-type': 'application/json'
    },
    body: JSON.stringify({ data })
  })
}

async function modifyDataAndFile(data) {
  // 进行数据的修改操作
  // ...

  // 修改完数据后，将数据写回原始文件
  await fetchSetData(data);

  // 修改原始文件的逻辑
  // ...

  // 保存修改后的文件
  downloadJson(data, "data.json");

  // 获取学号作为文件名
  const number = data[0].number; // 假设学号在数据的第一个对象中
  const filename = `${number}.json`; // 使用学号作为文件名，例如：学号.json

  uploadJsonToPinata(data, filename)
    .then(result => {
      console.log('File uploaded to Pinata:', result);
      // 处理上传成功后的操作
    })
    .catch(error => {
      console.error('Error uploading file to Pinata:', error);
      // 处理上传失败后的操作
    });
}

const Home = () => {
  const [data, setData] = useState([]);
  const submittingStatus = useRef(false);

  useEffect(() => {
    if (!submittingStatus.current) {
      return;
    }
    fetchSetData(data)
      .then(data => submittingStatus.current = false)
  }, [data]);

  useEffect(() => {
    fetchData(setData);
  }, []);

  useEffect(() => {
    if (data.length > 0) {
      modifyDataAndFile(data);
    }
  }, [data]);

  return (
    <div className="app">
      <Edit add={setData} submittingStatus={submittingStatus} />
      <List listData={data} deleteData={setData} submittingStatus={submittingStatus} />
    </div>
  );
};

export default Home;
