const Item = ({ id, attributes, deleteData, addItem, submittingStatus }) => {
    function deleteItem() {
      submittingStatus.current = true;
      deleteData(prev => prev.filter(item => item.id !== id));
    }

    const number = attributes.find(attr => attr.trait_type === "number")?.value;
    const name = attributes.find(attr => attr.trait_type === "name")?.value;
    const dep = attributes.find(attr => attr.trait_type === "department")?.value;
    const address = attributes.find(attr => attr.trait_type === "address")?.value;

    return (
      <div className="item">
        <div>
          <h1 style={{ color: 'red' }}>{"請確認您的資料輸入正確"}</h1>

          <p>{"學號: " + number}</p>
          <p>{"姓名: " + name}</p>
          <p>{"系所: " + dep}</p>
          <p>{"錢包地址: " + address}</p>
        </div>
        <button onClick={deleteItem} className="remove">刪除</button>
      </div>
    );
  };

  export default Item;
