import Edit from "./Edit";

const Item = ({ id, attributes, name, deleteData, submittingStatus }) => {
  function deleteItem() {
    submittingStatus.current = true;
    deleteData(function (prev) {
      return prev.filter((item) => item.id !== id);
    });
  }

  const numberAttribute = attributes.find(attr => attr.trait_type === 'number');
  const depAttribute = attributes.find(attr => attr.trait_type === 'department');
  const addressAttribute = attributes.find(attr => attr.trait_type === 'address');

  return (
    <div className="item">
      <div>
        <h1 style={{ color: "red" }}>{"請確認您的資料輸入正確"}</h1>

        <p>學號: {numberAttribute && numberAttribute.value}</p>
        <p>姓名: {name}</p>
        <p>系所: {depAttribute && depAttribute.value}</p>
        <p>錢包地址: {addressAttribute && addressAttribute.value}</p>
      </div>
      <button onClick={deleteItem} className="remove">
        刪除
      </button>
    </div>
  );
};

export default Item;
