import { useState, useEffect, useRef } from "react";
import { API_GET_DATA } from '../../global/constants';
import axios from 'axios'

import Edit from "./components/Edit";
import List from "./components/List";
import "./index.css";

const PINATA_API_KEY = '8d48fd61cabd7e7ec49d';
const PINATA_SECRET_API_KEY = 'b9683820d5d19707b404269951cf586941efc22bc5c05c80eb46f3a3e2082234';

async function fetchData(setData) {
  const res = await fetch(API_GET_DATA);
  const { data } = await res.json();
  setData(data);
}

async function fetchSetData(data) {
  await fetch(API_GET_DATA, {
    method: "PUT",
    headers: {
      'Content-type': 'application/json'
    },
    body: JSON.stringify({ data })
  });
}

const Home = () => {
  const [data, setData] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const submittingStatus = useRef(false);

  const addItem = (item) => {
    submittingStatus.current = true;
    setData((prevData) => {
      const newData = [
        {
          id: item.id,
          number: item.number,
          name: item.name,
          dep: item.dep,
          address: item.address,
        },
        ...prevData,
      ];

      fetchSetData(item);

      return newData;
    });
  };

  const downloadJson = () => {
    const jsonData = JSON.stringify(data);
    const blob = new Blob([jsonData], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "data.json";
    link.click();
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const uploadJsonToIPFS = async () => {
    if (!selectedFile) {
      console.error("請選擇一個檔案");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("https://api.pinata.cloud/pinning/pinFileToIPFS", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          pinata_api_key: PINATA_API_KEY,
          pinata_secret_api_key: PINATA_SECRET_API_KEY,
        },
      });

      if (response.status === 200) {
        const { IpfsHash } = response.data;
        console.log("JSON 檔案成功上傳到 IPFS，IPFS Hash:", IpfsHash);
      } else {
        console.error("上傳 JSON 檔案到 IPFS 失敗:", response.data);
      }
    } catch (error) {
      console.error("上傳 JSON 檔案到 IPFS 失敗:", error);
    }
  };

  useEffect(() => {
    fetchData(setData);
  }, []);

  return (
    <div className="app">
      <Edit add={addItem} submittingStatus={submittingStatus} />
      <List listData={data} deleteData={setData} submittingStatus={submittingStatus} />
      <button onClick={downloadJson}>下載 JSON 檔案</button>
      <input type="file" onChange={handleFileUpload} />
      <button onClick={uploadJsonToIPFS}>上傳 JSON 檔案到 IPFS</button>
    </div>
  );
};

export default Home;
