import Edit from "./Edit";

const Item = ({ id, number, name, dep, address, deleteData, submittingStatus }) => {
  function deleteItem() {
    submittingStatus.current = false;
    deleteData(function (prev) {
      return prev.filter((item) => item.id !== id);
    });
  }

  function handleInputChange(field, value) {
    // 在這裡處理輸入值的變化
    console.log(field, value); // 您可以在這裡對接收到的值進行處理
  }

  return (
    <div className="item">
      <div>
        <h1 style={{ color: "red" }}>{"請確認您的資料輸入正確"}</h1>
        <p>學號: {number}</p>
        <p>姓名: {name}</p>
        <p>系所: {dep}</p>
        <p>錢包地址: {address}</p>
        <Edit onInputChange={handleInputChange} /> {/* 傳遞回調函數給 Edit 元件 */}
      </div>
      <button onClick={deleteItem} className="remove">
        刪除
      </button>
    </div>
  );
};

export default Item;
