import { useState, useEffect, useRef } from "react";
import { API_GET_DATA } from '../../global/constants'
import Edit from "./components/Edit";
import List from "./components/List";
import "./index.css";

function downloadJson(jsonData, filename) {
  const jsonStr = JSON.stringify(jsonData);
  const blob = new Blob([jsonStr], { type: "application/json" });

  const link = document.createElement("a");
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();

  URL.revokeObjectURL(link.href);
}

async function uploadJsonToPinata(jsonData, num) {
    const apiKey = '8d48fd61cabd7e7ec49d';
    const apiSecret = 'b9683820d5d19707b404269951cf586941efc22bc5c05c80eb46f3a3e2082234';

    const url = 'https://api.pinata.cloud/pinning/pinJSONToIPFS';

    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        pinata_api_key: apiKey,
        pinata_secret_api_key: apiSecret,
      },
      body: JSON.stringify(jsonData),
    });

    const result = await response.json();
    return result;
  }


async function fetchData(setData) {
  const res = await fetch(API_GET_DATA)
  const { data } = await res.json()
  setData(data)
}

async function fetchSetData(data) {
  await fetch(API_GET_DATA, {
    method: "PUT",
    headers: {
      'Content-type': 'application/json'
    },
    body: JSON.stringify({ data })
  })
}

const Home = () => {
  const [data, setData] = useState([]);
  const submittingStatus = useRef(false);

  useEffect(() => {
    if (!submittingStatus.current) {
      return
    }
    fetchSetData(data)
      .then(data => submittingStatus.current = false)
  }, [data])

  useEffect(() => {
    fetchData(setData)
  }, [])


  useEffect(() => {
    if (data.length > 0) {
      downloadJson(data, "data.json");
      const filename = `${data[0].num}.json`; // 使用第一条数据的 number 字段作为文件名
      uploadJsonToPinata(data, filename)
        .then(result => {
          console.log('File uploaded to Pinata:', result);
          // 处理上传成功后的操作
        })
        .catch(error => {
          console.error('Error uploading file to Pinata:', error);
          // 处理上传失败后的操作
        });
    }
  }, [data]);


  return (
    <div className="app">
      <Edit add={setData} submittingStatus={submittingStatus} />
      <List listData={data} deleteData={setData} submittingStatus={submittingStatus} />
    </div>
  );
};

export default Home;
